import React, { useState } from 'react';
import ThisSeasonComponent from './ThisSeasonComponent';
import PastSeasonsComponent from './PastSeasonsComponent';
import TeamsComponent from './TeamsComponent';
import PlayersComponent from './PlayersComponent';

const LeagueHomePage = () => {
  const [activeTab, setActiveTab] = useState('thisSeason');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div>
      <div className="tab-buttons">
        <button
          onClick={() => handleTabClick('thisSeason')}
          className={activeTab === 'thisSeason' ? 'active' : ''}
        >
          This Season
        </button>
        <button
          onClick={() => handleTabClick('pastSeasons')}
          className={activeTab === 'pastSeasons' ? 'active' : ''}
        >
          Past Seasons
        </button>
        <button
          onClick={() => handleTabClick('teams')}
          className={activeTab === 'teams' ? 'active' : ''}
        >
          Teams
        </button>
        <button
          onClick={() => handleTabClick('players')}
          className={activeTab === 'players' ? 'active' : ''}
        >
          Players
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'thisSeason' && <ThisSeasonComponent />}
        {activeTab === 'pastSeasons' && <PastSeasonsComponent />}
        {activeTab === 'teams' && <TeamsComponent />}
        {activeTab === 'players' && <PlayersComponent />}
      </div>
    </div>
  );
};

export default LeagueHomePage;
