import React, { useState, useEffect } from 'react';
import api from './api';
import { useNavigate } from 'react-router-dom';


const Leagues = () => {
  const [leagues, setLeagues] = useState([]);
  const navigate = useNavigate()
  useEffect(() => {
    // Make the API call to fetch the list of leagues
    api.get('/leagues/?is_open=false')
      .then(response => {
        setLeagues(response.data);
      })
      .catch(error => {
        console.error('Error fetching leagues:', error);
      });
  }, []); // Empty dependency array ensures the effect runs only once

  const handleLeagueClick = (slug) => {
    navigate(`/league/${slug}`); // Navigate to the league route with the provided slug
  };
  return (
    <div>
      <h2>Leagues List</h2>
      <ul>
        {leagues.map(league => (
          <li key={league.league_id}> 
            <button   onClick={() => handleLeagueClick(league.name)}>{league.name}</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Leagues;
